import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit{

author={
  name:'',
  lastname:'',
  email:'',
  password:'',
  about:''
}
image:any;
constructor(){

}

ngOnInit(): void {

}
select(event:any){
console.log(event);
this.image = event.detail.files[0];
console.log(this.image);
}
register(){
  alert("inscription avec succés");
}
}
<div class="container mt-4">


    <h2 class="text-center"> Create author account </h2>

    <div class="col-6 mx-auto">



        <input [(ngModel)]="author.name" type="text" class="form-control m-1" placeholder="name">
        <input [(ngModel)]="author.lastname" type="text" class="form-control m-1" placeholder="lastname">
        <input [(ngModel)]="author.email" type="email" class="form-control m-1" placeholder="email">
        <input [(ngModel)]="author.password" type="password" class="form-control m-1" placeholder="password">
        <input  type="file" (change)="select($event)" class="form-control m-1" placeholder="image">
        <textarea [(ngModel)]="author.about" name="" id="" cols="30" rows="4" placeholder="about" class="form-control m-1" ></textarea>

        <button (click)="register()" class="btn btn-dark form-control m-1">Register</button>


    </div>

</div>
