        <input  type="password" class="form-control m-1" placeholder="password">
        <input type="file"  class="form-control m-1" placeholder="image">
        <textarea  name="" id="" cols="30" rows="4" placeholder="about" class="form-control m-1" ></textarea>

        <button  class="btn btn-dark form-control m-1">Register</button>


    </div>

</div>
