import { Component ,OnInit} from '@angular/core';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

author={
  email:'',
  password:''
}
token:any;

  constructor(private authService:AuthService,private router:Router){

  }


  ngOnInit(){

  }
  login(){
    this.authService.login(this.author).subscribe((res)=>{
      console.log(res);
      this.token = res;
      console.log(this.token);
      console.log(JSON.parse(this.token));
      localStorage.setItem('token',this.token.myToken);
      this.router.navigate(['/home']);
      //this.router.navigate(["/login"]);
    },(err)=>{
      console.log(err);
      //this.router.navigate(["/login"]);
    });
  }
}
<div class="container mt-4">


    <h2 class="text-center"> Login to author account </h2>

    <div class="col-6 mx-auto">




        <input [(ngModel)]="author.email"  type="text" class="form-control m-1" placeholder="email">
        <input [(ngModel)]="author.password"  type="password" class="form-control m-1" placeholder="password">

        <button (click)="login()" class="btn btn-dark form-control m-1">Login</button>


    </div>

</div>
